img = imread("lenna.png");[lin col] = size(img);out = [];img = uint16(img);for i = 2:(lin - 1)  for j = 2:(col - 1)    %out(i,j) = (img(i - 1,j - 1) + img(i - 1,j) + img(i - 1,j + 1) +    %            img(i    ,j - 1) + img(i    ,j) + img(i    ,j + 1) +     %            img(i + 1,j - 1) + img(i + 1,j) + img(i + 1,j + 1)) / 9;                    out(i,j) = (img(i - 1,j) + img(i,j - 1) + img(i,j) + img(i,j + 1) + img(i + 1,j)) / 5;  endfor;endfor;out = uint8(out);