#Rotaçãofunction out = efeitoOnda(img)  out = [];               # Inicializa variável de retorno    [lin, col] = size(img);    [x, y] = meshgrid(1:col, 1:lin); # Faz uma combinação dos valores de lin e col    #    Gera um vetor de novas coordenadas onde:  #     |x1 x2 x3 x4 são posições no eixo x       xlin*col|  #     |y1 y2 y3 y4 são posições no eixo y       ylin*col|  #     |1  1  1  1  é para facilitar os cálculos 1lin*col|    ncoord = [(x(:)' + 20 * sin(2 * pi * y(:)' / 80)); y(:)'; ones(1, lin * col) ];    # Transforma cada vetor em uma matriz de tamanho lin col  nX = reshape(ncoord(1,:), lin, col);  nY = reshape(ncoord(2,:), lin, col);    # Interpola os dados das 3 matrizes (x, y, img), no eixo nX e nY  out = interp2(x, y, double(img), nX, nY);    # Transforma-a para inteiros  out = uint8(out);endfunction