#Rotaçãofunction out = rotacionar(img, ang)  out = [];               # Inicializa variável de retorno    [lin, col] = size(img);    [x, y] = meshgrid(1:col, 1:lin); # Faz uma combinação dos valores de lin e col    matRotacao = [cosd(ang) -sind(ang) 0;                sind(ang) cosd(ang)  0;                0         0          1]; #matriz de rotação    #    Gera um vetor de novas coordenadas onde:  #     |x1 x2 x3 x4 são posições no eixo x       xlin*col|  #     |y1 y2 y3 y4 são posições no eixo y       ylin*col|  #     |1  1  1  1  é para facilitar os cálculos 1lin*col|    ncoord = matRotacao * [x(:)'; y(:)'; ones(1, lin * col)];    # Transforma cada vetor em uma matriz de tamanho lin col  nX = reshape(ncoord(1,:), lin, col);  nY = reshape(ncoord(2,:), lin, col);    # Interpola os dados das 3 matrizes (x, y, img), no eixo nX e nY  out = interp2(x, y, double(img), nX, nY);    # Transforma-a para inteiros  out = uint8(out);endfunction