function nimg = bps(img)  nimg = img;    out = bitshift(img, -7);  out = bitshift(out, 7);    nimg = [nimg out];    out = bitshift(img, -6);  out = bitshift(out, 7);    nimg = [nimg out];    out = bitshift(img, -5);  out = bitshift(out, 7);    nimg = [nimg out];    out = bitshift(img, -4);  out = bitshift(out, 7);    nimg = [nimg out];    out = bitshift(img, -3);  out = bitshift(out, 7);    nimg = [nimg out];    out = bitshift(img, -2);  out = bitshift(out, 7);    nimg = [nimg out];    out = bitshift(img, -1);  out = bitshift(out, 7);    nimg = [nimg out];    figure; imshow(nimg);  endfunction