function nimg = blend(img1, img2)  nimg = [img1 img2];  out = [];    [lin, col] = size(img1);    centro = lin / 2;    for i = 1:lin    for j = 1:col      out(i, j) = ((((abs(2 * centro - i - j)) / (2 * centro)) * img1(i,j)) + ((1 - ((abs(2 * centro - i - j)) / (2 * centro))) * img2(i,j)));    endfor  endfor    nimg = [nimg out];    figure; imshow(nimg);  endfunction