function [nimg, figs] = NoiseSum(img, n)  nimg = zeros(size(img));  [lin col] = size(img);  figs = cell(1,n);   mediana = [];    meio = floor(n / 2);    # Gera ruidos  for i = 1 : n    #figs{i} = imnoise(img);      !!!!!!!!!!!!!!!!!!!!!!!!!!    figs{i} = img;  endfor    # Calcula Mediana  for i = 1 : lin    for j = 1 : col      for k = 1 : n        mediana(k) = figs{k}(i, j);      endfor            sort(mediana, 1);      nimg(i, j) = mediana(meio);    endfor  endfor    figure; imshow(nimg);end