img  = imread("matches.png");mask = imread("matchMask.png");nimg = [];[lin, col] = size(img);nimg(:, :, 1) = img;nimg(:, :, 2) = img;nimg(:, :, 3) = img;nimg = uint8(nimg);inc = 50;for i = 1 : lin  for j = 1 : col    if (mask(i, j) == 0)      nimg(i, j, 1) = nimg(i, j, 1) + inc;      nimg(i, j, 2) = nimg(i, j, 2) + inc;      nimg(i, j, 3) = nimg(i, j, 3) + 2 * inc;    endif  endforendforfigure; imshow(nimg);