deslocamento = "t"; %Topimg  = imread("figC.png");[lin col camadas] = size(img);nimg = [];buffer = [];big_img = zeros(lin + 10, col + 10, 3);[nlin ncol, ~] = size(big_img);big_img(6:lin+5, 6:col+5, :) = img;if (deslocamento == "r")  mask = [0 0 0;           1 0 0;           0 0 0];    elseif (deslocamento == "a")  mask = [1 0 0;           0 0 0;           0 0 0];    elseif (deslocamento == "b")  mask = [0 1 0;           0 0 0;           0 0 0];    elseif (deslocamento == "c")  mask = [0 0 1;           0 0 0;           0 0 0];    elseif (deslocamento == "l")  mask = [0 0 0;           0 0 1;           0 0 0];    elseif (deslocamento == "d")  mask = [0 0 0;           0 0 0;           0 0 1];    elseif (deslocamento == "t")  mask = [0 0 0;           0 0 0;           0 1 0];    elseif (deslocamento == "e")  mask = [0 0 0;           0 0 0;           1 0 0];              display("NENHUMA OPCAO ESCOLHIDA!");    break;endiffor i = 6:lin+5  for j = 6:col+5    buffer(i, j, 1) = sum(sum(mask .* big_img(i-1:i+1, j-1:j+1, 1)));    buffer(i, j, 2) = sum(sum(mask .* big_img(i-1:i+1, j-1:j+1, 2)));    buffer(i, j, 3) = sum(sum(mask .* big_img(i-1:i+1, j-1:j+1, 3)));  endforendfornimg = buffer(6 : end, 6 : end, :); nimg = uint8(nimg);figure; imshow(nimg);