function nimg = imconv(img, tipo)  [lin col] = size(img);  nimg = [];  big_img = zeros(lin + 4, col + 4);  [blin bcol] = size(big_img);  big_img(3:lin+2, 3:col+2) = img;  big_img = double(big_img);  if (tipo == "z")        nimg = conv2(big_img, 3, "full");            elseif (tipo == "r")            for i = 3:lin+2        for j = 1:4          k = j;                    if (k == 3)            k = col + 2 + 1;          elseif(k == 4)            k = col + 2 + 2;          endif;                  if (k < 3)            big_img(i, k) = big_img(i, 3);          else            big_img(i, k) = big_img(i, col + 2);          endif        endfor      endfor                  for j = 1:bcol        for i = 1:4          k = i;                    if (k == 3)            k = lin + 2 + 1;          elseif(k == 4)            k = lin + 2 + 2;          endif;                  if (k < 3)            big_img(k, j) = big_img(3, j);          else            big_img(k, j) = big_img(lin + 2, j);          endif        endfor      endfor                  nimg = conv2(big_img, 3, "same");          else       display("Erro no tipo");      break;  endif  nimg = uint8(nimg);  figure; imshow(nimg);endfunction