img  = imread("figC.png");mask = imread("figMask.png");fator = 50;[lin col camadas] = size(img);nimg = img;for i = 1:lin  for j = 1:col    if (mask(i,j) > 1)      nimg(i, j, 1) = abs(nimg(i, j, 1) + 2 * fator);      nimg(i, j, 2) = abs(nimg(i, j, 2) - fator);      nimg(i, j, 3) = abs(nimg(i, j, 3) -  2 * fator);    endif  endforendfornimg = [img nimg];figure; imshow(nimg);